INSERT INTO public.services (id, name, description, base_price, created_at, updated_at, min_area, max_area, unit, is_peak_time_fee, special_discount) VALUES (6, 'Dọn dẹp nhà mới xây, sau sửa chữa', 'Công trình mới hoàn thành thường có nhiều bụi bẩn, vết sơn và mảnh vụn xây dựng. Dịch vụ của chúng tôi sẽ làm sạch toàn diện từ sàn nhà, tường, trần, cửa kính đến các ngóc ngách, giúp bạn có không gian sạch đẹp để dọn vào ở ngay.', 150000, '2025-03-04 20:44:10.504336', '2025-03-04 20:44:10.504336', 10, 200, '0 - 50m2', false, 'Ưu đãi 20% cho khách hàng lần đầu');
INSERT INTO public.services (id, name, description, base_price, created_at, updated_at, min_area, max_area, unit, is_peak_time_fee, special_discount) VALUES (2, 'Dọn phòng bếp', 'Giữ căn bếp luôn sạch sẽ và an toàn với dịch vụ dọn phòng bếp chuyên nghiệp. Chúng tôi làm sạch bề mặt bếp, lau chùi tủ bếp, rửa bồn rửa chén, loại bỏ dầu mỡ bám lâu ngày, dọn dẹp rác thải và khử mùi giúp căn bếp luôn gọn gàng, vệ sinh.', 120000, '2025-03-03 14:09:26.763319', '2025-03-03 14:09:26.763319', 10, 200, '0 -100m2', false, 'Ưu đãi 20% cho khách hàng lần đầu');
INSERT INTO public.services (id, name, description, base_price, created_at, updated_at, min_area, max_area, unit, is_peak_time_fee, special_discount) VALUES (1, 'Dọn phòng khách', 'Dịch vụ dọn phòng khách giúp không gian tiếp khách của bạn luôn sạch sẽ và gọn gàng. Chúng tôi lau chùi bàn ghế, quét bụi kệ tủ, làm sạch sàn nhà, hút bụi thảm, vệ sinh cửa kính và sắp xếp lại đồ đạc để mang đến không gian thoáng mát, sang trọng.', 150000, '2025-03-03 14:09:26.763319', '2025-03-03 14:09:26.763319', 10, 200, '0 - 50m2', false, 'Ưu đãi 20% cho khách hàng lần đầu');
INSERT INTO public.services (id, name, description, base_price, created_at, updated_at, min_area, max_area, unit, is_peak_time_fee, special_discount) VALUES (3, 'Dọn phòng ngủ', 'Phòng ngủ sạch sẽ giúp bạn có giấc ngủ ngon và thư giãn hơn. Dịch vụ của chúng tôi bao gồm thay ga giường, hút bụi nệm và rèm cửa, lau chùi tủ quần áo, bàn trang điểm, quét dọn và khử mùi để tạo không gian nghỉ ngơi thoải mái nhất.', 150000, '2025-03-04 20:44:10.504336', '2025-03-04 20:44:10.504336', 10, 200, '0 - 50m2', false, 'Ưu đãi 20% cho khách hàng lần đầu');
INSERT INTO public.services (id, name, description, base_price, created_at, updated_at, min_area, max_area, unit, is_peak_time_fee, special_discount) VALUES (4, 'Dọn nhà vệ sinh', 'Nhà vệ sinh là khu vực cần được làm sạch kỹ lưỡng để đảm bảo vệ sinh và sức khỏe. Chúng tôi tẩy rửa bồn cầu, bồn rửa mặt, lau gương, cọ sàn, khử trùng và khử mùi hôi giúp nhà vệ sinh luôn thơm tho và an toàn.', 150000, '2025-03-04 20:44:10.504336', '2025-03-04 20:44:10.504336', 10, 200, '0 - 50m2', false, 'Ưu đãi 20% cho khách hàng lần đầu');
